import React, { useState } from "react";
import { useAppContext } from "../context/appContext";
import { useNavigate } from "react-router-dom";

const AgregarLibro = () => {
  // Evita submit con Enter y mueve al siguiente input
  const handleInputKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      const form = e.target.form;
      const index = Array.prototype.indexOf.call(form, e.target);
      if (form.elements[index + 1]) {
        form.elements[index + 1].focus();
      }
    }
  };

  const { store, actions } = useAppContext();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    isbn: "",
    titulo: "",
    autor: "",
    editorial: "",
    stock: 1,
    precio: 0,
    ubicacion: "",
  });

  const [sinIsbn, setSinIsbn] = useState(false);
  const [isbnGenerado, setIsbnGenerado] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [datosCargados, setDatosCargados] = useState(false);
  const [origen, setOrigen] = useState("");
  const [generandoIsbn, setGenerandoIsbn] = useState(false);

  const mensaje = store.mensaje;

  const limpiarDatosLibro = (isbnValue = formData.isbn) => {
    setFormData({
      isbn: isbnValue,
      titulo: "",
      autor: "",
      editorial: "",
      stock: 1,
      precio: 0,
      ubicacion: "",
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });

    if (name === "isbn") {
      setOrigen("");
      setDatosCargados(false);
      limpiarDatosLibro(value);
    }

    actions.setMensaje("");
  };

  const generarYMostrarIsbn = async () => {
    try {
      setGenerandoIsbn(true);
      actions.setMensaje("Generando ISBN autom√°ticamente...");

      const resultado = await actions.generarIsbnAutomatico();

      if (resultado.success) {
        setIsbnGenerado(resultado.isbn);
        setFormData({
          ...formData,
          isbn: resultado.isbn,
        });
        actions.setMensaje(`‚úÖ ISBN generado autom√°ticamente: ${resultado.isbn}`);
        setOrigen("servidor");
      } else {
        actions.setMensaje(`‚ùå ${resultado.error}`);
        setOrigen("");
        setSinIsbn(false);
        setFormData({
          ...formData,
          isbn: "",
        });
      }
    } catch (error) {
      console.error("Error al generar ISBN:", error);
      actions.setMensaje("‚ùå Error inesperado al generar ISBN.");
      setOrigen("");
      setSinIsbn(false);
    } finally {
      setGenerandoIsbn(false);
    }
  };

  const handleAutocomplete = async () => {
    const { isbn } = formData;
    if (!isbn) return;

    setIsLoading(true);
    setOrigen("");

    try {
      const libroLocal = await actions.buscarLibroPorISBN(isbn);

      if (libroLocal) {
        setFormData({
          ...formData,
          autor: libroLocal.autor || "",
          editorial: libroLocal.editorial || "",
          stock: libroLocal.stock || 1,
          precio: libroLocal.precio || 0,
          titulo: libroLocal.titulo || "",
          ubicacion: libroLocal.ubicacion || "",
        });
        setOrigen("local");
        setDatosCargados(true);
      } else {
        const libroExterno = await actions.buscarLibroExterno(isbn);

        if (libroExterno) {
          setFormData({
            ...formData,
            titulo: libroExterno.titulo || "",
            autor: libroExterno.autor || "",
            editorial: libroExterno.editorial || "",
          });
          setOrigen("externo");
          setDatosCargados(true);
          actions.setMensaje(
            `Datos obtenidos de ${libroExterno.fuente || "fuente externa"}. Puede editar si es necesario.`
          );
        } else {
          actions.setMensaje(
            "No se encontr√≥ informaci√≥n para este ISBN. Puede ingresar los datos manualmente."
          );
          setOrigen("");
          setDatosCargados(false);
          limpiarDatosLibro(isbn);
        }
      }
    } catch (error) {
      console.error("Error al autocompletar los datos:", error);
      actions.setMensaje("Hubo un error al buscar informaci√≥n del libro.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleIsbnBlur = () => {
    if (formData.isbn && !datosCargados && !sinIsbn) {
      handleAutocomplete();
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { isbn, titulo, autor, ubicacion, stock } = formData;

    if (!isbn || !titulo || !autor || !ubicacion) {
      alert("Por favor, complete los campos obligatorios: ISBN, t√≠tulo, autor y ubicaci√≥n.");
      return;
    }

    if (stock < 1) {
      alert("El stock debe ser al menos 1.");
      return;
    }

    if (formData.precio < 0) {
      alert("El precio no puede ser negativo.");
      return;
    }

    try {
      const libroExistente = await actions.buscarLibroPorISBN(formData.isbn);

      if (libroExistente) {
        let cambios = [];

        if (formData.titulo !== libroExistente.titulo) cambios.push("t√≠tulo");
        if (formData.autor !== libroExistente.autor) cambios.push("autor");
        if (formData.editorial !== libroExistente.editorial) cambios.push("editorial");
        if (parseInt(formData.stock) !== parseInt(libroExistente.stock))
          cambios.push(`stock (${libroExistente.stock} ‚Üí ${formData.stock})`);
        if (parseFloat(formData.precio) !== parseFloat(libroExistente.precio)) cambios.push("precio");
        if (formData.ubicacion !== libroExistente.ubicacion) cambios.push("ubicaci√≥n");

        if (cambios.length > 0) {
          const resultado = await actions.actualizarLibro(libroExistente.id, formData);

          if (resultado.success) {
            const mensajeExito = `Libro actualizado con √©xito. Campos modificados: ${cambios.join(", ")}.`;
            actions.setMensaje(mensajeExito);
            setTimeout(() => actions.setMensaje(""), 10000);
            setOrigen("local");
          } else {
            alert(resultado.error || "Hubo un error al actualizar el libro");
          }
        } else {
          actions.setMensaje("No se realizaron cambios en el libro.");
          setTimeout(() => actions.setMensaje(""), 10000);
        }
      } else {
        const resultado = await actions.crearLibro(formData);

        if (resultado.success) {
          actions.setMensaje(`Libro creado con √©xito con stock de ${formData.stock} unidad(es).`);
          setTimeout(() => actions.setMensaje(""), 10000);

          setFormData({
            isbn: "",
            titulo: "",
            autor: "",
            editorial: "",
            stock: 1,
            precio: 0,
            ubicacion: "",
          });
          setOrigen("");
          setDatosCargados(false);
          setSinIsbn(false);
          setIsbnGenerado("");
        } else {
          alert(resultado.error || "Hubo un error al crear el libro");
        }
      }
    } catch (error) {
      console.error("Error en la solicitud:", error);
      alert("Hubo un error con la solicitud: " + error.message);
    }
  };

  return (
    <div className="container p-4 bg-light border rounded shadow-lg">
      <div className="mb-4 text-center">
        <h2 className="fw-bold text-primary">üìö Crear Nuevo Libro</h2>
        {mensaje && <div className="alert alert-info mt-3">{mensaje}</div>}
      </div>

      <form onSubmit={handleSubmit} className="row g-3">
        <div className="col-12 col-md-6">
          <label htmlFor="isbn" className="form-label">ISBN:</label>
          <div className="input-group">
            <input
              type="text"
              id="isbn"
              name="isbn"
              className="form-control"
              value={formData.isbn}
              onChange={handleChange}
              onBlur={handleIsbnBlur}
              onKeyDown={handleInputKeyDown}
              readOnly={sinIsbn}
              required
              placeholder={sinIsbn ? "Se generar√° autom√°ticamente..." : "Ingrese ISBN"}
            />
            {isLoading && !sinIsbn && (
              <span className="input-group-text">
                <div className="spinner-border spinner-border-sm" role="status" />
              </span>
            )}
            {!isLoading && origen && (
              <span className={`input-group-text text-white ${origen === "local" ? "bg-success" :
                  origen === "servidor" ? "bg-primary" :
                    origen === "fallback" ? "bg-warning text-dark" :
                      "bg-info"
                }`}>
                {origen === "local" ? "BD Local" :
                  origen === "servidor" ? "Servidor" :
                    origen === "fallback" ? "Fallback" :
                      "API Externa"}
              </span>
            )}
          </div>
          <div className="form-check mt-2">
            <input
              className="form-check-input"
              type="checkbox"
              checked={sinIsbn}
              onChange={async (e) => {
                const checked = e.target.checked;
                if (checked) {
                  await generarYMostrarIsbn();
                  setSinIsbn(true);
                  setDatosCargados(false);
                } else {
                  setFormData({ ...formData, isbn: "" });
                  setIsbnGenerado("");
                  setOrigen("");
                  setDatosCargados(false);
                  setSinIsbn(false);
                  actions.setMensaje("");
                }
              }}
              disabled={generandoIsbn}
              id="crearSinIsbn"
            />
            <label htmlFor="crearSinIsbn" className="form-check-label">Crear sin ISBN</label>
            {generandoIsbn && (
              <span className="ms-2 spinner-border spinner-border-sm" role="status" />
            )}
          </div>
        </div>

        <div className="col-12 col-md-6">
          <label htmlFor="titulo" className="form-label">T√≠tulo:</label>
          <input
            type="text"
            id="titulo"
            name="titulo"
            className="form-control"
            value={formData.titulo}
            onChange={handleChange}
            required
            onKeyDown={handleInputKeyDown}
          />
        </div>

        <div className="col-12 col-md-6">
          <label htmlFor="autor" className="form-label">Autor:</label>
          <input
            type="text"
            id="autor"
            name="autor"
            className="form-control"
            value={formData.autor}
            onChange={handleChange}
            required
            onKeyDown={handleInputKeyDown}
          />
        </div>

        <div className="col-12 col-md-6">
          <label htmlFor="editorial" className="form-label">Editorial:</label>
          <input
            type="text"
            id="editorial"
            name="editorial"
            className="form-control"
            value={formData.editorial}
            onChange={handleChange}
            onKeyDown={handleInputKeyDown}
          />
        </div>

        <div className="col-6 col-md-4">
          <label htmlFor="stock" className="form-label">Stock:</label>
          <input
            type="number"
            id="stock"
            name="stock"
            className="form-control"
            value={formData.stock}
            min="1"
            onChange={handleChange}
            onKeyDown={handleInputKeyDown}
          />
        </div>

        <div className="col-6 col-md-4">
          <label htmlFor="precio" className="form-label">Precio:</label>
          <input
            type="number"
            id="precio"
            name="precio"
            className="form-control"
            value={formData.precio}
            onChange={handleChange}
            onKeyDown={handleInputKeyDown}
          />
        </div>

        <div className="col-12 col-md-4">
          <label htmlFor="ubicacion" className="form-label">Ubicaci√≥n:</label>
          <input
            type="text"
            id="ubicacion"
            name="ubicacion"
            className="form-control"
            value={formData.ubicacion}
            onChange={handleChange}
            onKeyDown={handleInputKeyDown}
          />
        </div>

        <div className="d-flex justify-content-between mt-4">
          <button type="submit" className="btn btn-success btn-lg w-50 me-2">Guardar</button>
          <button
            type="button"
            className="btn btn-outline-secondary btn-lg w-50"
            onClick={() => {
              setFormData({
                isbn: "",
                titulo: "",
                autor: "",
                editorial: "",
                stock: 1,
                precio: 0,
                ubicacion: "",
              });
              setOrigen("");
              setDatosCargados(false);
              setSinIsbn(false);
              setIsbnGenerado("");
              actions.setMensaje("");
            }}
          >
            Limpiar
          </button>
        </div>

        <div className="mt-4">
          <button
            type="button"
            className="btn btn-outline-dark w-100"
            onClick={() => navigate("/")}
          >
            Volver al inicio
          </button>
        </div>
      </form>
    </div>
  );
};

export default AgregarLibro;
